import{_ as e,c as t,o as r,a3 as a}from"./chunks/framework.CNR3DNuL.js";const u=JSON.parse('{"title":"快速了解","description":"","frontmatter":{},"headers":[],"relativePath":"guide/quick-understanding.md","filePath":"guide/quick-understanding.md","lastUpdated":1715381724000}'),o={name:"guide/quick-understanding.md"},s=a('<h1 id="快速了解" tabindex="-1">快速了解 <a class="header-anchor" href="#快速了解" aria-label="Permalink to &quot;快速了解&quot;">​</a></h1><p>了解自己使用的或者想使用的工具特性很有必要，因为每个人的用途和想法都不同，可能是工作需要，也可能以学习为目的...</p><h2 id="🍒-features" tabindex="-1">🍒 Features <a class="header-anchor" href="#🍒-features" aria-label="Permalink to &quot;🍒 Features&quot;">​</a></h2><h3 id="快速开发" tabindex="-1">🚀 快速开发 <a class="header-anchor" href="#快速开发" aria-label="Permalink to &quot;:rocket: 快速开发&quot;">​</a></h3><p>本项目使用 Monorepo 方式管理，这意味着在新开项目的时候，可以一定程度上减少重复造轮子（<s>相应的增加学(hua)习(shui)的时间</s>），但是采用本框架的时候需要自行调研这轮子用着顺不顺手以及会不会用，还有团队的接受程度等。</p><p>本着框架无关的原则，Vben3 不与任何框架深度集成，开发者可以选择自己中意的框架进行开发🥰。</p><p>并不是所有的项目都需要使用该种方式管理，就像不是所有项目都必须使用微服务一样。</p><h3 id="turborepo-架构" tabindex="-1">🌿 Turborepo 架构 <a class="header-anchor" href="#turborepo-架构" aria-label="Permalink to &quot;:herb: Turborepo 架构&quot;">​</a></h3><p>采用比Monorepo更优秀的Turborepo架构，提升构建体验😎</p><div class="info custom-block"><p class="custom-block-title">提示</p><p>点击 <a href="https://turbo.build/repo/docs/core-concepts/monorepos" target="_blank" rel="noreferrer">此处</a> 查看Turborepo对Monorepo的优势</p></div><h2 id="🌐-浏览器支持" tabindex="-1">🌐 浏览器支持 <a class="header-anchor" href="#🌐-浏览器支持" aria-label="Permalink to &quot;🌐 浏览器支持&quot;">​</a></h2><p><strong>本地开发</strong> 推荐使用最新版的 <code>Chrome</code> 浏览器，<strong>不支持</strong><code>Chrome 80</code>以下版本。</p><p><strong>生产环境</strong> 支持现代浏览器，不支持 IE。</p><table><thead><tr><th style="text-align:center;"><a href="http://godban.github.io/browsers-support-badges/" target="_blank" rel="noreferrer"><img src="https://raw.githubusercontent.com/alrra/browser-logos/master/src/archive/internet-explorer_9-11/internet-explorer_9-11_48x48.png" alt="IE" width="24px" height="24px"></a>IE</th><th style="text-align:center;"><a href="http://godban.github.io/browsers-support-badges/" target="_blank" rel="noreferrer"><img src="https://raw.githubusercontent.com/alrra/browser-logos/master/src/edge/edge_48x48.png" alt=" Edge" width="24px" height="24px"></a>Edge</th><th style="text-align:center;"><a href="http://godban.github.io/browsers-support-badges/" target="_blank" rel="noreferrer"><img src="https://raw.githubusercontent.com/alrra/browser-logos/master/src/firefox/firefox_48x48.png" alt="Firefox" width="24px" height="24px"></a>Firefox</th><th style="text-align:center;"><a href="http://godban.github.io/browsers-support-badges/" target="_blank" rel="noreferrer"><img src="https://raw.githubusercontent.com/alrra/browser-logos/master/src/chrome/chrome_48x48.png" alt="Chrome" width="24px" height="24px"></a>Chrome</th><th style="text-align:center;"><a href="http://godban.github.io/browsers-support-badges/" target="_blank" rel="noreferrer"><img src="https://raw.githubusercontent.com/alrra/browser-logos/master/src/safari/safari_48x48.png" alt="Safari" width="24px" height="24px"></a>Safari</th></tr></thead><tbody><tr><td style="text-align:center;">not support</td><td style="text-align:center;">last 2 versions</td><td style="text-align:center;">last 2 versions</td><td style="text-align:center;">last 2 versions</td><td style="text-align:center;">last 2 versions</td></tr></tbody></table>',14),n=[s];function i(h,l,d,p,c,g){return r(),t("div",null,n)}const _=e(o,[["render",i]]);export{u as __pageData,_ as default};
